.lp-counter-section:after {content: "";background-color: rgba(44,44,44,0.87);position: absolute;left: 0;top: 0;width: 100%;height: 100%;z-index: -1;}
.lp-counter-text {color: #fff;font-size: 50px;line-height: 1.1;}
hr.hr-yellow {border: 0;border-top: 3px solid #ffe400;width: 50px;margin: 36px auto;}
.lp-counter-box {color: #fff;text-transform: uppercase;}

/*********************************************************** Testimonial Section ************************************************************/
.lp-testimonial-section {text-align: center;padding: 91px 0;}
.lp-slider-content {padding: 71px 60px;}
.lp-slider-client-detail {justify-content: center;}
.lp-sec-title {color:#ffe400}
.lp-ctext {margin-left: 15px;}
.lp-ctext h3 , .lp-ctext h5 {font-style: italic;font-weight: 300;line-height:1;margin-bottom:0;}
.lp-ctext h3 {margin:0 0 7px;}
.lp-slider-content em {position:relative;}
.lp-slider-content em:before { content: ''; position: absolute; left: -72px; top: -8px; width: 86px; height: 57px; content: close-quote; content: open-quote; font-size: 150px; line-height: 0; color: #ffe400; font-style: normal; font-weight: 700; font-family: sans-serif; }

/*************************************************** footer contact details ******************************************************/
.lp-footer-section {position: relative;z-index: 1;padding: 86px 0 53px;color:#fff;}
.lp-footer-section:after {content: "";background-color: rgba(44,44,44,0.87);position: absolute;left: 0;top: 0;width: 100%;height: 100%;z-index: -1;}
.lp-footer-contact-rich {text-align:center;margin-bottom:50px;position:relative;}
.lp-footer-contact-rich:after { position: absolute; width: 2px; background-color: #fff; height: 79px; content: ""; right: 0; top: 0; }
.lp-footer-contact-rich:last-child:after {display:none;}
.lp-footer-contact-rich a {color:#fff;display:block;font-size:25px;}
.lp-footer-contact-rich i {font-size:45px;margin-bottom:45px;display:block;}
.lp-footer-section p {color:#fff;text-align:center;line-height:1.6;}
.lp-footer-section p:last-child {margin-bottom:0;}
.lp-footer-contact-rich a:hover {color:#ffe400;}

/*************************************************** footer ******************************************************/
.lo-footer-copy {background-color:#000;text-align:center;padding:32px 0;}
.lo-footer-copy p {font-style:italic;color:#000;margin:0;color:#fff;}

/*--------------- Responsive media query style start here ----------------*/

@media (max-width: 1300px){
    .container2 {padding:0 20px;}
}

@media (max-width: 1250px){
    .container {padding:0 20px;}
    .lp-nav-contact {padding-right:0;}
}

@media (max-width: 1200px){
   .lp-banner-section h1 {max-width:100%;text-align:center;margin:0 auto 55px;}
    h1 {font-size:45px;}
    h2 {font-size:32px;}
    .lp-form-title h2 br {display:none;}
}

@media (max-width: 1100px){
    /* header text */
    h1 {font-size:40px;}
    h2 {font-size:30px;}
    h3 {font-size:26px;}
    h4 {font-size:24px;}
    h5 {font-size:18px;}
    em {font-size:20px;}
    p,a,li {font-size:15px;}
}

@media (max-width: 1024px){
    .lp-counter-text {font-size:42px;}
    .lp-counter-section {padding: 160px 0 80px;}
}

@media (max-width: 980px){
    fieldset.form-columns-2 > div,fieldset.form-columns-2 > div:last-child {width:100% !important;margin-right:0 !important;}
    .lp-nav-contact span {font-size:22px;}
    .h-logo {padding-top:5px;}
    .lp-form-section {padding:30px 25px 18px;}
    .lp-counter-box {width:50%;margin-bottom:25px;}
}

@media (max-width: 767px){
     /* header text */
    h1 {font-size:32px;}
    h2 {font-size:26px;}
    h3 {font-size:22px;}
    h4 {font-size:20px;}
    h5 {font-size:16px;}
    em {font-size:18px;}
    /* p tag */
    p {font-size: 14px; }
    /* links */
    a {font-size: 14px;}
    /* ul li */
    ul li ,ol li {  font-size: 14px; }
    select, textarea, input[type=color], input[type=date], input[type=datetime-local], input[type=datetime], input[type=email], input[type=month], input[type=number], input[type=password], input[type=search], input[type=tel], input[type=text], input[type=time], input[type=url], input[type=week], textarea 
    {height:40px;font-size:14px;}
    textarea {height:80px;}
    .hs-form-field { margin-bottom: 12px; }
    .dis-flex {display:block;}
    .lp-focus-img {display:none !important;}
    .lp-form-section {padding:50px;}
    .lp-form-title {text-align:center;}
    .lp-banner-section2>div>div.row-fluid {display:block;}
    .lp-form-section {margin:0;}
    .lp-banner-section {padding:50px 0;}
    .hs_submit input {padding: 10px 40px;font-size:16px;}
    .hs_submit,.one-line-text {text-align:center;}
    .lp-banner-section h1 {margin: 0 auto 40px;}
    .h-logo ,.lp-nav-contact {width:50% !important;float:left !important;} 
    .h-logo img {max-width:150px;}
    .lp-nav-contact a {font-size: 20px;}
    .lp-nav-contact span {font-size:10px;}
    .lp-nav-contact {margin-right:0;}
    .lp-nav-contact i {font-size:30px;}
    .lp-counter-text {font-size:30px;}
    .lp-cimg {margin-bottom:15px;}
    .lp-slider-content { padding: 50px 30px; }
    .lp-slider-content em:before {left: -43px;top: -35px;width: 40px;height: 40px;background-size: contain;background-repeat: no-repeat;}
    .lp-footer-contact-rich i {font-size: 30px;margin-bottom: 20px;}
    .lp-footer-contact-rich a {font-size:22px;}
    .lp-footer-contact-rich:after {display:none;}
    .lp-testimonial-section {padding:70px 0;}
    .lp-counter-section { padding: 80px 0 60px; }
    .lp-nav-contact a span {display:none;}
    .lp-nav-contact {padding-top: 7px;}
    .lp-nav  {padding:15px 0;}
    .lp-form-section {padding: 50px 20px;}
    .reg-form h4 {height:auto;}
}

@media (max-width: 640px){
      /* header text */
    h1 {font-size:30px;}
    h2 {font-size:25px;}
    h3 {font-size:20px;}
    h4 {font-size:18px;}
    h5 {font-size:15px;}
    em {font-size:16px;}

    br {display:none;}
    .form-columns-2 .hs-form-field .hs-input {width:100% !important;}
    .lp-form-section {padding:30px;}
    .lp-counter-box {width:100%;}
    hr.hr-yellow {margin:15px auto;}
    .lp-counter-box {margin-bottom:40px;}
    .hs_submit input {width:100%;}
    .lp-nav-contact a {font-size:0;}
}


@media (max-width: 480px){
    .lp-testimonial-section {padding:50px 0;}
    .lp-footer-section {padding: 50px 0;}
    .lp-counter-section { padding: 50px 0; }
}

@media (max-width: 374px){
    .lp-form-section { padding: 30px 20px; }
}
